import ReactDOM from 'react-dom';
import './index.css';
import {App,data} from './App';
import reportWebVitals from './reportWebVitals';

function re_render(){
  ReactDOM.render(
    <React.StrictMode>
      <App />
    </React.StrictMode>,
    document.getElementById('root')
  );
}

var index1=[];
var index2=[];

function sort_data()
{
  let N = data.length;

  for(let i=0;i<N-1;i=i+1)
