import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import {App,data} from './App';
import reportWebVitals from './reportWebVitals';

function re_render(){
  ReactDOM.render(
    <React.StrictMode>
      <App />
    </React.StrictMode>,
    document.getElementById('root')
  );
}

var arr_data = [];
function sort_data()
{
  let N = data.length;

  for(let i=0;i<N-1;i=i+1)
  {
    for(let j=0;j<N-1;j++)
    {
      if(data[j+1] < data[j])
      {
        let temp = data[j+1];
        data[j+1]=data[j];
        data[j] = temp;
        arr_data.push(data.slice()); 

      }
    }

  }
}


re_render();
sort_data();

let k=0;
let myVar = setInterval(function() {

  if(k===arr_data.length)
  clearInterval(myVar)

  else
  {
    for(let i=0;i<data.length;i=i+1)
    {
      data[i]=arr_data[k][i];
    }

    k=k+1;

    re_render();
  }

}, 500);





